function initChart2(canvas, width, height,dpr) {   
    const chart = echarts.init(canvas, null, {    
        width: width,
        height: height,
        devicePixelRatio: dpr 
       
    });
    canvas.setChart(chart);
    console.log(app.globalData.newList)
    console.log(app.globalData.originList)
    console.log(app.globalData.highestList)

    var option = {
      tooltip: {
        //雷达图的tooltip不会超出画布，也可以设置position属性，position定位的tooltip 不会随着鼠标移动而位置变化，不友好
        confine: true,
        enterable: true,  //鼠标是否可以移动到tooltip区域内
        trigger: 'item',
        // backgroundColor: 'rgba(0,0,250,0.2)', //tooltip背景颜色
        formatter: function (params) {
          console.log(params.name)
          console.log(params.data)
          return params.name+'\n(计划)P: '+params.data.value[0]+'\n(注意力)A: '+params.data.value[1]+'\n(同时性)S: '+params.data.value[2]+'\n(继时性)S: '+params.data.value[3]
        }
      },
      legend: {
          data:['初次','最新','最高']
      },
      radar:[
        {
          name: {
            textStyle: {
                color: '#fff',
                backgroundColor: '#999',
                borderRadius: 3,
                padding: [3, 5]
            }
          },
          splitArea: { // 坐标轴在 grid 区域中的分隔区域，默认不显示。
            show: true,
            areaStyle: { // 分隔区域的样式设置。
                color: ['rgba(0,0,250,0.1)', '#fff'],
                // 分隔区域颜色。分隔区域会按数组中颜色的顺序依次循环设置颜色。默认是一个深浅的间隔色。
            }
        },
          indicator: [
            {text: 'P(计划)', max: 100},
            {text: 'A(注意力)', max: 100},
            {text: 'S(同时性)', max: 100},
            {text: 'S(继时性)', max: 100}
        ],
        radius: 120,
        splitNumber: 4,
        
      }

      ],
      series:[
        {
          type: 'radar',
          tooltip: {
              trigger: 'item',
              // backgroundColor: 'rgba(0,0,250,0.2)',
              textStyle:{
                align:'left'
            }
          },
          // areaStyle: {},
          itemStyle: { // 折线拐点标志的样式。
            normal: { // 普通状态时的样式
                lineStyle: {
                    width: 1
                },
                opacity: 0.2
            },
            emphasis: { // 高亮时的样式
                lineStyle: {
                    width: 3
                },
                opacity: 1
            }
        },
          data: [
              {
                  value: app.globalData.originList,
                  name: '初次'
              } ,
              {
                    value: app.globalData.newList,
                    name: '最新'
              },
                {
                    value: app.globalData.highestList,
                    name: '最高'
                }
          ]
      }
      ]
  };
    chart.setOption(option);
    return chart;
}
